import React, { useState } from 'react';
import { Row, Col, Container, Form, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function Calculator() {
  const [insurancePremium, setInsurancePremium] = useState(0);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [addOns, setAddOns] = useState({
    claimsDefault: false,
    cyberRisks: false,
    lossKeys: false,
    courtesyDamage: false,
    pureFinancialLoss: false,
    sportsLeisureDamage: false,
  });

  const handlePlanSelect = (plan) => {
    let basePremium = 0;
    if (plan === 'plan1') basePremium = 200;
    if (plan === 'plan2') basePremium = 500;
    if (plan === 'plan3') basePremium = 800;

    let addOnPremium = Object.values(addOns).filter(Boolean).length * 100;

    setSelectedPlan(plan);
    setInsurancePremium(basePremium + addOnPremium);
  };

  const handleAddOnChange = (event) => {
    const { id, checked } = event.target;
    setAddOns(prevState => {
      const newAddOns = { ...prevState, [id]: checked };
      let basePremium = 0;
      if (selectedPlan === 'plan1') basePremium = 200;
      if (selectedPlan === 'plan2') basePremium = 500;
      if (selectedPlan === 'plan3') basePremium = 800;

      let addOnPremium = Object.values(newAddOns).filter(Boolean).length * 100;

      setInsurancePremium(basePremium + addOnPremium);

      return newAddOns;
    });
  };

  return (
    <Container className="Calculator">
      <Row>
        <Col>
          <h1>Insurance calculator</h1>
        </Col>
      </Row>
      <br />
      <Row>
        <Col>
          <h3>View our plans.</h3>
        </Col>
      </Row>
      <br />
      <Row>
        <Col className="plan-col">
          <div className="plan">
            <h2>Plan 1</h2>
            <p className="price">CHF 200/month</p>
            <h3>Includes</h3>
            <ul>
              <li>Personal Injury</li>
              <li>Property Damage</li>
              <li>Financial Loss</li>
            </ul>
            <Button onClick={() => handlePlanSelect('plan1')}>Select Plan 1</Button>
          </div>
        </Col>
        <Col className="plan-col">
          <div className="plan">
            <h2>Plan 2</h2>
            <p className="price">CHF 500/month</p>
            <h3>Includes</h3>
            <ul>
              <li>Personal Injury</li>
              <li>Property Damage</li>
              <li>Financial Loss</li>
              <li>Rental Property Damage</li>
              <li>Damages Caused by Children</li>
              <li>Good Samaritan Acts</li>
            </ul>
            <Button onClick={() => handlePlanSelect('plan2')}>Select Plan 2</Button>
          </div>
        </Col>
        <Col className="plan-col">
          <div className="plan">
            <h2>Plan 3</h2>
            <p className="price">CHF 800/month</p>
            <ul>
              <li>Personal Injury</li>
              <li>Property Damage</li>
              <li>Financial Loss</li>
              <li>Rental Property Damage</li>
              <li>Damages Caused by Children</li>
              <li>Good Samaritan Acts</li>
              <li>Key Loss</li>
              <li>Internet Damage</li>
              <li>Pet Damage</li>
            </ul>
            <Button onClick={() => handlePlanSelect('plan3')}>Select Plan 3</Button>
          </div>
        </Col>
      </Row>
      <br />
      <h2>Optional Add-ons</h2>
      <Row>
        <Col>
          <Form.Check 
            type="checkbox" 
            id="claimsDefault" 
            label="Coverage for Claims Default" 
            checked={addOns.claimsDefault}
            onChange={handleAddOnChange}
          />
        </Col>
        <Col>
          <Form.Check 
            type="checkbox" 
            id="cyberRisks" 
            label="Cyber Risks" 
            checked={addOns.cyberRisks}
            onChange={handleAddOnChange}
          />
        </Col>
        <Col>
          <Form.Check 
            type="checkbox" 
            id="lossKeys" 
            label="Loss of Keys" 
            checked={addOns.lossKeys}
            onChange={handleAddOnChange}
          />
        </Col>
      </Row>

      <Row>
        <Col>
          <Form.Check 
            type="checkbox" 
            id="courtesyDamage" 
            label="Courtesy Damage" 
            checked={addOns.courtesyDamage}
            onChange={handleAddOnChange}
          />
        </Col>
        <Col>
          <Form.Check 
            type="checkbox" 
            id="pureFinancialLoss" 
            label="Pure Financial Loss" 
            checked={addOns.pureFinancialLoss}
            onChange={handleAddOnChange}
          />
        </Col>
        <Col>
          <Form.Check 
            type="checkbox" 
            id="sportsLeisureDamage" 
            label="Sports and Leisure Damage" 
            checked={addOns.sportsLeisureDamage}
            onChange={handleAddOnChange}
          />
        </Col>
      </Row>
      <br />
      <Row>
        <Col>
          <h3>Total Insurance Premium: CHF {insurancePremium}</h3>
        </Col>
      </Row>
    </Container>
  );
}

export default Calculator;
