import React, { useState } from 'react';
import { Form, Row, Col, OverlayTrigger } from 'react-bootstrap';
import questionMark from '../images/question.png';

function Checkout() {
  const [paymentMethod, setPaymentMethod] = useState('');

  const handlePaymentChange = (event) => {
    setPaymentMethod(event.target.value);
  };

  const renderTooltip = (props) => (
    <Tooltip id="button-tooltip" {...props}>
      Hier ist eine Beschreibung der Zahlungsmethode.
    </Tooltip>
  );

  const handleQuestionMark = () => {
    return(paymentMethod)
  }

  return (
    <div className='Checkout'>
      <h1>Checkout</h1>
      <br />
      <Form id='payment'>
        <Row className="mb-3 align-items-center">
          <Col xs="auto">
            <Form.Check
              onClick={handlePaymentChange}
              inline
              label="Pay by check"
              name="paymentMethod"
              type={'radio'}
              id="inline-radio-1"
              value="Pay by check"
            />
          </Col>
          <Col xs="auto">
            <OverlayTrigger
            placement="right"
            delay={{ show: 250, hide: 400 }}
            overlay={renderTooltip}
            >
            <img
              className='questionMark'
              src={questionMark}
              alt="Question Mark"
            />
            </OverlayTrigger>

          </Col>
        </Row>
        <Row className="mb-3 align-items-center">
          <Col xs="auto">
            <Form.Check
              onClick={handlePaymentChange}
              inline
              label="Credit-/Debitcard"
              name="paymentMethod"
              type={'radio'}
              id="inline-radio-2"
              value="Credit-/Debitcard"
            />
          </Col>
        </Row>
        <Row className="mb-3 align-items-center">
          <Col xs="auto">
            <Form.Check
              onClick={handlePaymentChange}
              inline
              label="TWINT"
              name="paymentMethod"
              type={'radio'}
              id="inline-radio-3"
              value="TWINT"
            />
          </Col>
          <Col xs="auto">
            <img
              className='questionMark'
              src={questionMark}
              alt="Question Mark"
            />
          </Col>
        </Row>
        <Row>
          <Col xs="auto">
            <Form.Check
              onClick={handlePaymentChange}
              inline
              label="Apple Pay"
              name="paymentMethod"
              type={'radio'}
              id="inline-radio-4"
              value="Apple Pay"
            />
          </Col>
        </Row>
      </Form>
      <br />
      <p>Selected payment method: {paymentMethod}</p>
    </div>
  );
};

export default Checkout;
